
public class Dateof {

	int dd;
	int mm;
	int yy;
	
	Dateof(){
		dd=0;
		mm=0;
		yy=0;
	}
	public Dateof(int dd, int mm, int yy) {
	
		this.dd=dd;
		this.mm=mm;
		this.yy=yy;
		// TODO Auto-generated constructor stub
	}
	public int getDd() {
		return dd;
	}
	public void setDd(int dd) {
		this.dd = dd;
	}
	public int getMm() {
		return mm;
	}
	public void setMm(int mm) {
		this.mm = mm;
	}
	public int getYy() {
		return yy;
	}
	public void setYy(int yy) {
		this.yy = yy;
	}
	
}
